function misha_remove_fields( $woo_checkout_fields_array ) {

	// she wanted me to leave these fields in checkout
	// unset( $woo_checkout_fields_array['billing']['billing_first_name'] );
	// unset( $woo_checkout_fields_array['billing']['billing_last_name'] );
	 //unset( $woo_checkout_fields_array['billing']['billing_phone'] );
 //unset( $woo_checkout_fields_array['billing']['billing_email'] );
 //unset( $woo_checkout_fields_array['order']['order_comments'] ); // remove order notes

	// and to remove the billing fields below
	unset( $woo_checkout_fields_array['billing']['billing_company'] ); // remove company field
	unset( $woo_checkout_fields_array['billing']['billing_country'] );
	unset( $woo_checkout_fields_array['billing']['billing_address_1'] );
	unset( $woo_checkout_fields_array['billing']['billing_address_2'] );
	unset( $woo_checkout_fields_array['billing']['billing_city'] );
	unset( $woo_checkout_fields_array['billing']['billing_state'] ); // remove state field
	unset( $woo_checkout_fields_array['billing']['billing_postcode'] ); // remove zip code field

	return $woo_checkout_fields_array;
}

add_filter( 'woocommerce_checkout_fields', 'misha_remove_fields', 9999 );

function woo_remove_product_tabs( $tabs ) {
    unset( $tabs['additional_information'] );
	 unset( $tabs['reviews'] );
    return $tabs;
}
add_filter( 'woocommerce_product_tabs', 'woo_remove_product_tabs', 98 );

// Part 1: assign fee

add_action( 'woocommerce_cart_calculate_fees', 'bbloomer_add_checkout_fee_for_gateway' );

function bbloomer_add_checkout_fee_for_gateway() {
    $chosen_gateway = WC()->session->get( 'chosen_payment_method' );
     if ( $chosen_gateway == 'paypal' ) {


WC()->cart->add_fee( 'Gateway Fee', WC()->cart->get_cart_contents_total()*0.04  );
   }
	     if ( $chosen_gateway == 'stripe' ) {


WC()->cart->add_fee( 'Stripe Fee', WC()->cart->get_cart_contents_total()*0.04  );
   }

	     if ( $chosen_gateway == 'razorpay' ) {


WC()->cart->add_fee( 'GST 18%', WC()->cart->get_cart_contents_total()*0.18  );
   }
}

// Part 2: reload checkout on payment gateway change

add_action( 'woocommerce_review_order_before_payment', 'bbloomer_refresh_checkout_on_payment_methods_change' );

function bbloomer_refresh_checkout_on_payment_methods_change(){
    ?>
    <script type="text/javascript">
        (function($){
            $( 'form.checkout' ).on( 'change', 'input[name^="payment_method"]', function() {
                $('body').trigger('update_checkout');
            });
        })(jQuery);
    </script>
    <?php
}
